import * as React from 'react';
import Box from '@mui/material/Box';
import { DataGrid, GridToolbar } from '@mui/x-data-grid';
import { darken, lighten } from '@mui/material/styles';
import axios from 'axios';
import Link from '@mui/material/Link';
import { Outlet } from 'react-router-dom';

  
  const handleCellClick = (param, event) => {
    event.stopPropagation();
  };
  
  const handleRowClick = (param, event) => {
    event.stopPropagation();
  };
  
const getBackgroundColor = (color, mode) =>
  mode === 'dark' ? darken(color, 0.6) : lighten(color, 0.6);

const getHoverBackgroundColor = (color, mode) =>
  mode === 'dark' ? darken(color, 0.5) : lighten(color, 0.5);

  const columns=[
    { field: 'device_ID', headerName: 'شماره دستگاه',width:150},
    { field: 'MAC', headerName: 'شماره مک',width:150},
    { field: 'POS', headerName: 'پوز',width:90},
    { field: 'PRINTER', headerName: 'پرینتر',width:90},
    { field: 'CUTTER', headerName: 'کاتر',width:90},
    { field: 'NUMBER', headerName: 'تعداد بلیط',width:90},
    { field: 'DateTime', headerName: 'زمان ثبت',width:150},
    { field: 'lastUpdate', headerName: 'آخرین گذارش',width:150},
    {
        field: "Route",
        renderCell: (cellValues) => {
          return <Link href={`${cellValues.row.device_ID}`}>Link</Link>;
        }
      }
];
const baseURL = "http://127.0.0.1:8000/api/devices/";
const drawerWidth = 240;


export default function StylingRowsGrid() {
    const [device, setDevice] = React.useState(null);
    React.useEffect(() => {
        axios.get(baseURL).then((response) => {
        setDevice(response.data);
        });
    }, []);
    if (!device) return null;
  return (
    <><Box
          sx={{
              height: 550,
              width: `calc(100% - ${drawerWidth}px)`,
              marginLeft: 25,
              '& .super-app-theme--1': {
                  bgcolor: (theme) => getBackgroundColor(theme.palette.error.main, theme.palette.mode),
                  '&:hover': {
                      bgcolor: (theme) => getHoverBackgroundColor(theme.palette.error.main, theme.palette.mode),
                  },
              },
          }}
      >
          <DataGrid getRowId={(row) => row.device_ID}
              columns={columns}
              rows={device}
              sx={{
                  bgcolor: 'secondary.main',
                  boxShadow: 2,
                  border: 2,
                  borderColor: 'primary.light',
                  '& .MuiDataGrid-cell:hover': {
                      color: 'primary.main',
                  },
              }}
              components={{ Toolbar: GridToolbar }}
              getRowClassName={(params) => `super-app-theme--${params.row.status}`}
              onCellClick={handleCellClick}
              onRowClick={handleRowClick} />
      </Box><Outlet />
      <script>
        window.setInterval('refresh()', 100000); 	
        function refresh() {
          window.location.reload()
        }
      </script></>
  );
}
