import * as React from 'react';
import { DataGrid, GridToolbar, gridClasses } from '@mui/x-data-grid';
import axios from 'axios';
import { alpha, styled } from '@mui/material/styles';


const ODD_OPACITY = 0.4;

const StripedDataGrid = styled(DataGrid)(({ theme }) => ({
  [`& .${gridClasses.row}.even`]: {
    backgroundColor: theme.palette.primary.main,
    '&:hover, &.Mui-hovered': {
      backgroundColor: alpha(theme.palette.primary.main, ODD_OPACITY),
      '@media (hover: none)': {
        backgroundColor: 'transparent',
      },
    },
    '&.Mui-selected': {
      backgroundColor: alpha(
        theme.palette.primary.main,
        ODD_OPACITY + theme.palette.action.selectedOpacity,
      ),
      '&:hover, &.Mui-hovered': {
        backgroundColor: alpha(
          theme.palette.primary.main,
          ODD_OPACITY +
            theme.palette.action.selectedOpacity +
            theme.palette.action.hoverOpacity,
        ),
        // Reset on touch devices, it doesn't add specificity
        '@media (hover: none)': {
          backgroundColor: alpha(
            theme.palette.primary.main,
            ODD_OPACITY + theme.palette.action.selectedOpacity,
          ),
        },
      },
    },
  },
}));

const columns=[
    { field: 'device_ID', headerName: 'شماره دستگاه',width:150},
    { field: 'MAC', headerName: 'شماره مک',width:150},
    { field: 'POS', headerName: 'پوز',width:90},
    { field: 'PRINTER', headerName: 'پرینتر',width:90},
    { field: 'CUTTER', headerName: 'کاتر',width:90},
    { field: 'NUMBER', headerName: 'تعداد بلیط',width:90},
    { field: 'DateTime', headerName: 'زمان ثبت',width:150},
    { field: 'lastUpdate', headerName: 'آخرین گذارش',width:150},
];


const baseURL = "http://127.0.0.1:8000/devices/";
const drawerWidth = 240;

export default function ExportDefaultToolbar() {

    const [device, setDevice] = React.useState(null);

    React.useEffect(() => {
        axios.get(baseURL).then((response) => {
        setDevice(response.data);
        });
    }, []);

    if (!device) return null;
    console.log(device[2])

    return (
    <div style={{ height: 550, width: `calc(100% - ${drawerWidth}px)`, marginLeft:200,}}>
        <StripedDataGrid getRowId={(row) => row.device_ID}
        columns={columns}
        rows={device}
        sx={{
            bgcolor: 'secondary.main',
            boxShadow: 2,
            border: 2,
            borderColor: 'primary.light',
            '& .MuiDataGrid-cell:hover': {
              color: 'primary.main',
            },
          }}
        components={{ Toolbar: GridToolbar }}
        getRowClassName={(params) =>
            params.indexRelativeToCurrentPage % 2 === 0 ? 'even' : 'odd'
          } />
        </div>
    );
}
